patch:
diff --git a/ext/session/session.c b/ext/session/session.c
index b23f1fce358..9df92c81076 100644
--- a/ext/session/session.c
+++ b/ext/session/session.c
@@ -1056,24 +1056,46 @@ PS_SERIALIZER_ENCODE_FUNC(php) /* {{{ */
 {
 	smart_str buf = {0};
 	php_serialize_data_t var_hash;
-	PS_ENCODE_VARS;
+	zend_string *key;
+	zend_ulong num_key;
+	zval *struc;
+	zval session_array_copy;
+	zval *session_array_ptr = NULL;
+
+	if (Z_ISREF(PS(http_session_vars)) && Z_TYPE_P(Z_REFVAL(PS(http_session_vars))) == IS_ARRAY) {
+		session_array_ptr = Z_REFVAL(PS(http_session_vars));
+		ZVAL_COPY(&session_array_copy, session_array_ptr);
+	} else {
+		ZVAL_UNDEF(&session_array_copy);
+	}

 	PHP_VAR_SERIALIZE_INIT(var_hash);

-	PS_ENCODE_LOOP(
-		smart_str_appendl(&buf, ZSTR_VAL(key), ZSTR_LEN(key));
-		if (memchr(ZSTR_VAL(key), PS_DELIMITER, ZSTR_LEN(key))) {
-			PHP_VAR_SERIALIZE_DESTROY(var_hash);
-			smart_str_free(&buf);
-			return NULL;
-		}
-		smart_str_appendc(&buf, PS_DELIMITER);
-		php_var_serialize(&buf, struc, &var_hash);
-	);
+	if (Z_TYPE(session_array_copy) == IS_ARRAY) {
+		HashTable *_ht = Z_ARRVAL(session_array_copy);
+		ZEND_HASH_FOREACH_KEY(_ht, num_key, key) {
+			if (key == NULL) {
+				php_error_docref(NULL, E_WARNING, "Skipping numeric key " ZEND_LONG_FMT, num_key);
+				continue;
+			}
+			if ((struc = php_get_session_var(key))) {
+				smart_str_appendl(&buf, ZSTR_VAL(key), ZSTR_LEN(key));
+				if (memchr(ZSTR_VAL(key), PS_DELIMITER, ZSTR_LEN(key))) {
+					PHP_VAR_SERIALIZE_DESTROY(var_hash);
+					smart_str_free(&buf);
+					zval_ptr_dtor(&session_array_copy);
+					return NULL;
+				}
+				smart_str_appendc(&buf, PS_DELIMITER);
+				php_var_serialize(&buf, struc, &var_hash);
+			}
+		} ZEND_HASH_FOREACH_END();
+	}

 	smart_str_0(&buf);

 	PHP_VAR_SERIALIZE_DESTROY(var_hash);
+	zval_ptr_dtor(&session_array_copy);
 	return buf.s;
 }
 /* }}} */
