patch:
diff --git a/ext/session/session.c b/ext/session/session.c
index b23f1fce358..90315b17bd0 100644
--- a/ext/session/session.c
+++ b/ext/session/session.c
@@ -1060,16 +1060,34 @@ PS_SERIALIZER_ENCODE_FUNC(php) /* {{{ */

 	PHP_VAR_SERIALIZE_INIT(var_hash);

-	PS_ENCODE_LOOP(
-		smart_str_appendl(&buf, ZSTR_VAL(key), ZSTR_LEN(key));
-		if (memchr(ZSTR_VAL(key), PS_DELIMITER, ZSTR_LEN(key))) {
-			PHP_VAR_SERIALIZE_DESTROY(var_hash);
-			smart_str_free(&buf);
-			return NULL;
-		}
-		smart_str_appendc(&buf, PS_DELIMITER);
-		php_var_serialize(&buf, struc, &var_hash);
-	);
+	zval *session_vars_ref = &PS(http_session_vars);
+	zval *session_vars = Z_REFVAL_P(session_vars_ref);
+	if (Z_TYPE_P(session_vars) == IS_ARRAY) {
+		HashTable *ht_copy = zend_array_dup(Z_ARRVAL_P(session_vars));
+		zend_string *key;
+		zend_ulong num_key;
+		zval *struc;
+		ZEND_HASH_FOREACH_KEY_VAL(ht_copy, num_key, key, struc) {
+			if (key == NULL) {
+				php_error_docref(NULL, E_WARNING,
+					"Skipping numeric key " ZEND_LONG_FMT, num_key);
+				continue;
+			}
+			smart_str_appendl(&buf, ZSTR_VAL(key), ZSTR_LEN(key));
+			if (memchr(ZSTR_VAL(key), PS_DELIMITER, ZSTR_LEN(key))) {
+				PHP_VAR_SERIALIZE_DESTROY(var_hash);
+				smart_str_free(&buf);
+				zend_array_destroy(ht_copy);
+				return NULL;
+			}
+			smart_str_appendc(&buf, PS_DELIMITER);
+			zval tmp;
+			ZVAL_COPY(&tmp, struc);
+			php_var_serialize(&buf, &tmp, &var_hash);
+			zval_ptr_dtor(&tmp);
+		} ZEND_HASH_FOREACH_END();
+		zend_array_destroy(ht_copy);
+	}

 	smart_str_0(&buf);
