responded: I can see that bit 7 (1<<7) is available. Let me create a patch that adds a flag to prevent hash table resizing during sorting:

args.patch:
diff --git a/Zend/zend_hash.c b/Zend/zend_hash.c
index 1c06104944f..1bcb8dd17b2 100644
--- a/Zend/zend_hash.c
+++ b/Zend/zend_hash.c
@@ -1099,7 +1099,10 @@ static zend_always_inline zval *_zend_hash_index_add_or_update_i(HashTable *ht,
 		}
 		ZEND_HASH_IF_FULL_DO_RESIZE(ht);		/* If the Hash table is full, resize it */
 	}
-
+	
+	if (UNEXPECTED(HT_FLAGS(ht) & HASH_FLAG_SORTING)) {
+		return NULL; /* Prevent resizing during sort */
+	}
 	idx = ht->nNumUsed++;
 	nIndex = h | ht->nTableMask;
 	p = ht->arData + idx;
@@ -2909,9 +2912,13 @@ ZEND_API void ZEND_FASTCALL zend_hash_sort_ex(HashTable *ht, sort_func_t sort, b
 		HT_HASH_RESET(ht);
 	}

+	/* Set sorting flag to prevent hash table modifications during sort */
+	HT_FLAGS(ht) |= HASH_FLAG_SORTING;
 	sort((void *)ht->arData, ht->nNumUsed, sizeof(Bucket), (compare_func_t) compar,
 			(swap_func_t)(renumber? zend_hash_bucket_renum_swap :
 				(HT_IS_PACKED(ht) ? zend_hash_bucket_packed_swap : zend_hash_bucket_swap)));
+	/* Clear sorting flag */
+	HT_FLAGS(ht) &= ~HASH_FLAG_SORTING;

 	ht->nInternalPointer = 0;

