+    uint32_t orig_num_used = ht->nNumUsed;
+    Bucket *tmp_buckets = NULL;

     IS_CONSISTENT(ht);
     HT_ASSERT_RC1(ht);

     if (!(ht->nNumOfElements>1) && !(renumber && ht->nNumOfElements>0)) {
