         if (PyErr_Occurred()) {
             return NULL;
         }
         prev_task = Py_None;
     }

-    if (task == Py_None) {
-        if (_PyDict_DelItem_KnownHash(state->current_tasks, loop, hash) == -1) {
-            return NULL;
-        }
-    } else {
-        if (_PyDict_SetItem_KnownHash(state->current_tasks, loop, task, hash) == -1) {
-            return NULL;
-        }
-    }
-
-    Py_INCREF(prev_task);
-
-    return prev_task;
+    /* INCREF prev_task before possibly deleting/replacing it in the dict */
