+    /* Remove any context from the freelist that is still in the call stack */
+    ProfilerContext *stack = pObj->currentProfilerContext;
+    while (stack) {
+        ProfilerContext **prev = &pObj->freelistProfilerContext;
+        while (*prev) {
+            if (*prev == stack) {
+                *prev = (*prev)->previous;
+                break;
+            }
+            prev = &(*prev)->previous;
+        }
+        stack = stack->previous;
+    }
