diff --git a/Objects/listobject.c b/Objects/listobject.c
index d09bb639103..4ae692f7ae3 100644
--- a/Objects/listobject.c
+++ b/Objects/listobject.c
@@ -3332,6 +3332,10 @@ list_traverse(PyObject *self, visitproc visit, void *arg)
 static PyObject *
 list_richcompare_impl(PyObject *v, PyObject *w, int op)
 {
+    /* Protect the list objects from being freed during recursive comparisons */
+    Py_INCREF(v);
+    Py_INCREF(w);
+    
     PyListObject *vl, *wl;
     Py_ssize_t i;

@@ -3382,7 +3386,11 @@ list_richcompare_impl(PyObject *v, PyObject *w, int op)
     }

     /* Compare the final item again using the proper operator */
-    return PyObject_RichCompare(vl->ob_item[i], wl->ob_item[i], op);
+    PyObject *result = PyObject_RichCompare(vl->ob_item[i], wl->ob_item[i], op);
+    Py_DECREF(v);
+    Py_DECREF(w);
+    return result;
+    
 }

 static PyObject *

