+++ b/Modules/_lsprof.c
@@ -765,7 +765,14 @@ flush_unmatched(ProfilerObject *pObj)
         if (pContext)
             PyMem_Free(pContext);
     }
-
+    /* Also free the freelist, as in clearEntries, to avoid use-after-free */
