-        else
-            pObj->currentProfilerContext = pContext->previous;
-        if (pContext)
-            PyMem_Free(pContext);
+        /* Move to previous before free to avoid use-after-free */
+        pObj->currentProfilerContext = pContext->previous;
+        PyMem_Free(pContext);
     }
+    /* Explicitly clear to NULL to avoid lingering pointer */
