Example of a patch format:
```diff
--- a/foo.c
+++ b/foo.c
@@ -11,7 +11,9 @@
}

int check (char *string) {
+   if (string == NULL) {
+       return 0;
+   }
-   return !strcmp(string, "hello");
+   return !strcmp(string, "hello world");
}
int main() {

