+        if (stdscr == NULL) {
+            _cursesmodule_state *state = get_cursesmodule_state(module);
+            PyErr_SetString(state->error, "stdscr is NULL; curses not properly initialized");
+            return NULL;
+        }
+        wrefresh(stdscr);
+        return (PyObject *)PyCursesWindow_New(stdscr, NULL);
     }
```

