 763| static PyObject *
 764| PyCursesWindow_New(WINDOW *win, const char *encoding)
 765| {
 766|     PyCursesWindowObject *wo;
 767| 
 768|     if (encoding == NULL) {
 769| #if defined(MS_WINDOWS)
 770|         char *buffer[100];
 771|         UINT cp;
 772|         cp = GetConsoleOutputCP();
 773|         if (cp != 0) {
 774|             PyOS_snprintf(buffer, sizeof(buffer), "cp%u", cp);
 775|             encoding = buffer;
 776|         }
 777| #elif defined(CODESET)
 778|         const char *codeset = nl_langinfo(CODESET);
 779|         if (codeset != NULL && codeset[0] != 0)
 780|             encoding = codeset;
 781| #endif
 782|         if (encoding == NULL)
 783|             encoding = "utf-8";
 784|     }
 785| 
 786|     wo = PyObject_New(PyCursesWindowObject, &PyCursesWindow_Type);
 787|     if (wo == NULL) return NULL;
 788|     wo->win = win;
 789|     wo->encoding = _PyMem_Strdup(encoding);
 790|     if (wo->encoding == NULL) {
 791|         Py_DECREF(wo);
 792|         PyErr_NoMemory();
 793|         return NULL;
 794|     }
 795|     return (PyObject *)wo;
 796| }
