+            curses_initscr_called = FALSE; // Defensive: mark as uninitialized
+            _cursesmodule_state *state = get_cursesmodule_state(module);
+            PyErr_SetString(state->error, "curses initscr previously called but stdscr is NULL (terminal initialization failed)");
+            return NULL;
+        }
+        // Defensive check: try-catch wrefresh in case stdscr is invalid
+        int r = 0;
+        Py_BEGIN_ALLOW_THREADS
+        r = wrefresh(stdscr);
+        Py_END_ALLOW_THREADS
+        if (r == ERR) {
+            curses_initscr_called = FALSE;
+            _cursesmodule_state *state = get_cursesmodule_state(module);
+            PyErr_SetString(state->error, "wrefresh failed; curses stdscr invalid or terminal lost");
+            return NULL;
+        }
+        return (PyObject *)PyCursesWindow_New(stdscr, NULL);
     }

     win = initscr();

-    if (win == NULL || stdscr == NULL) {
+    if (win == NULL || stdscr == NULL) {
+        curses_initscr_called = FALSE; // Defensive: mark as uninitialized
         _cursesmodule_state *state = get_cursesmodule_state(module);
         PyErr_SetString(state->error, catchall_NULL);
         return NULL;
     }
```

This patch disables the cached state if the window is missing, and checks for errors in the reused path as well as after initialization.

