+++ b/Modules/_cursesmodule.c
@@ -3255,7 +3255,10 @@ _curses_initscr_impl(PyObject *module)
     PyCursesWindowObject *winobj;

     if (initialised) {
-        wrefresh(stdscr);
+        if (stdscr != NULL) {
+            wrefresh(stdscr);
+        }
+        // Return current stdscr even if it's NULL - let the caller handle it
         return (PyObject *)PyCursesWindow_New(stdscr, NULL);
     }

