@@ -3255,13 +3255,18 @@ _curses_initscr_impl(PyObject *module)
     PyCursesWindowObject *winobj;

     if (initialised) {
+        if (stdscr == NULL) {
+            PyErr_SetString(PyCursesError, "stdscr is NULL: curses screen not initialized");
+            return NULL;
+        }
         wrefresh(stdscr);
