@@ -3255,8 +3255,17 @@ _curses_initscr_impl(PyObject *module)
     PyCursesWindowObject *winobj;

     if (initialised) {
+        if (stdscr == NULL) {
+            PyErr_SetString(PyCursesError, catchall_NULL);
+            return NULL;
+        }
         wrefresh(stdscr);
-        return (PyObject *)PyCursesWindow_New(stdscr, NULL);
+        PyObject *winobj = PyCursesWindow_New(stdscr, NULL);
+        if (winobj == NULL) {
+            // PyCursesWindow_New already sets an exception
+            return NULL;
+        }
+        return winobj;
     }

     win = initscr();

