+        PyErr_SetString(PyCursesError, "curses cannot initialize: TERM environment variable not set");
+        return NULL;
+    }
+    if (!isatty(fileno(stdout))) {
+        PyErr_SetString(PyCursesError, "curses cannot initialize: stdout is not a tty");
+        return NULL;
+    }
