patch:
diff --git a/Modules/_asynciomodule.c b/Modules/_asynciomodule.c
index 7483e9c0f43..81e0d6cc6a0 100644
--- a/Modules/_asynciomodule.c
+++ b/Modules/_asynciomodule.c
@@ -2967,8 +2967,17 @@ task_step_handle_result_impl(asyncio_state *state, TaskObj *task, PyObject *resu
         if (task->task_must_cancel) {
             PyObject *r;
             int is_true;
-            r = PyObject_CallMethodOneArg(result, &_Py_ID(cancel),
-                                             task->task_cancel_msg);
+            /* Get cancel method first to avoid use-after-free during method lookup */
+            PyObject *cancel_method = PyObject_GetAttr(result, &_Py_ID(cancel));
+            if (cancel_method == NULL) {
+                return NULL;
+            }
+            PyObject *cancel_msg = task->task_cancel_msg;
+            if (cancel_msg == NULL) {
+                cancel_msg = Py_None;
+            }
+            r = PyObject_CallOneArg(cancel_method, cancel_msg);
+            Py_DECREF(cancel_method);
             if (r == NULL) {
                 return NULL;
             }
@@ -3060,8 +3069,17 @@ task_step_handle_result_impl(asyncio_state *state, TaskObj *task, PyObject *resu
         if (task->task_must_cancel) {
             PyObject *r;
             int is_true;
-            r = PyObject_CallMethodOneArg(result, &_Py_ID(cancel),
-                                             task->task_cancel_msg);
+            /* Get cancel method first to avoid use-after-free during method lookup */
+            PyObject *cancel_method = PyObject_GetAttr(result, &_Py_ID(cancel));
+            if (cancel_method == NULL) {
+                return NULL;
+            }
+            PyObject *cancel_msg = task->task_cancel_msg;
+            if (cancel_msg == NULL) {
+                cancel_msg = Py_None;
+            }
+            r = PyObject_CallOneArg(cancel_method, cancel_msg);
+            Py_DECREF(cancel_method);
             if (r == NULL) {
                 return NULL;
             }
