patch:
diff --git a/Modules/arraymodule.c b/Modules/arraymodule.c
index b80c964f20d..c2cbab58544 100644
--- a/Modules/arraymodule.c
+++ b/Modules/arraymodule.c
@@ -3092,8 +3092,10 @@ array_arrayiterator___setstate__(arrayiterobject *self, PyObject *state)
         return NULL;
     if (index < 0)
         index = 0;
-    else if (index > Py_SIZE(self->ao))
+    else if (self->ao != NULL && index > Py_SIZE(self->ao))
         index = Py_SIZE(self->ao); /* iterator exhausted */
+    else if (self->ao == NULL)
+        index = 0; /* iterator already exhausted */
     self->index = index;
     Py_RETURN_NONE;
 }
