patch:
diff --git a/Modules/arraymodule.c b/Modules/arraymodule.c
index b80c964f20d..b63d51fd70d 100644
--- a/Modules/arraymodule.c
+++ b/Modules/arraymodule.c
@@ -3092,8 +3092,13 @@ array_arrayiterator___setstate__(arrayiterobject *self, PyObject *state)
         return NULL;
     if (index < 0)
         index = 0;
-    else if (index > Py_SIZE(self->ao))
+    else if (self->ao == NULL) {
+        /* Underlying array is gone, iterator is exhausted */
+        index = 0;
+    }
+    else if (index > Py_SIZE(self->ao)) {
         index = Py_SIZE(self->ao); /* iterator exhausted */
+    }
     self->index = index;
     Py_RETURN_NONE;
 }
