diff --git a/Python/import.c b/Python/import.c
index 9dec0f488a3..11bc9c9dc5b 100644
--- a/Python/import.c
+++ b/Python/import.c
@@ -3854,15 +3854,16 @@ PyImport_ImportModuleLevelObject(PyObject *name, PyObject *globals,
                 }

