patch:
diff --git a/src/execution/messages.cc b/src/execution/messages.cc
index 155e8003340..235ad814f34 100644
--- a/src/execution/messages.cc
+++ b/src/execution/messages.cc
@@ -528,8 +528,15 @@ MaybeHandle<String> MessageFormatter::TryFormat(
   if (!remaining_args.empty() &&
       std::count(std::begin(kTemplatesWithMismatchedArguments),
                  std::end(kTemplatesWithMismatchedArguments), index) == 0) {
-    FATAL("Too many arguments to template (expected %zu, got %zu): %s",
-          args.size() - remaining_args.size(), args.size(), template_string);
+    // Instead of crashing, append a generic error message or return a generic error string.
+    // This preserves program stability and aids fuzzing/security research.
+    // Old code:
+    // FATAL("Too many arguments to template (expected %zu, got %zu): %s",
+    //       args.size() - remaining_args.size(), args.size(), template_string);
+    builder.AppendCString("<error: too many arguments provided to template>");
+    // Optionally, return a generic error string:
+    // return isolate->factory()->InternalizeString(
+    //     base::StaticCharVector("<error>"));
   }

   return builder.Finish();
