diff --git a/src/objects/js-date-time-format.cc b/src/objects/js-date-time-format.cc
index c51ac6da12e..95d73754015 100644
--- a/src/objects/js-date-time-format.cc
+++ b/src/objects/js-date-time-format.cc
@@ -1686,10 +1686,16 @@ std::optional<std::string> GetOffsetTimeZone(Isolate* isolate,
     return tz;
   }
   int32_t p = 3;
+  if (p >= len) {
+    return std::nullopt;
+  }
   uint16_t m0 = flat.Get(p);
   if (m0 == ':') {
     // Ignore ':'
     p++;
+    if (p >= len) {
+      return std::nullopt;
+    }
     m0 = flat.Get(p);
   }
   if (len - p != 2) {
@@ -1697,6 +1703,9 @@ std::optional<std::string> GetOffsetTimeZone(Isolate* isolate,
     return std::nullopt;
   }
   uint16_t m1 = flat.Get(p + 1);
+  if (p + 1 >= len) {
+    return std::nullopt;
+  }
   if (m0 >= '0' && m0 <= '5' && m1 >= '0' && m1 <= '9') {
     tz += m0;
     tz += m1;
