patch:
diff --git a/src/help.c b/src/help.c
index 28d914c82..e1cf0539f 100644
--- a/src/help.c
+++ b/src/help.c
@@ -423,8 +423,15 @@ find_help_tags(
 							   && arg[2] != NUL)))
 	{
 	    STRCPY(d, "/\\\\");
-	    STRCPY(d + 3, arg + 1);
-	    // Check for "/\\_$", should be "/\\_\$"
+	    // Ensure we don't overflow the buffer when copying arg + 1
+	    if (STRLEN(arg + 1) < IOSIZE - 4)  // 4 bytes for "/\\\\" + null terminator
+	    {
+		STRCPY(d + 3, arg + 1);
+	    }
+	    else
+	    {
+		vim_strncpy(d + 3, arg + 1, IOSIZE - 4 - 1);
+	    }
 	    if (d[3] == '_' && d[4] == '$')
 		STRCPY(d + 4, "\\$");
 	}
@@ -450,7 +457,7 @@ find_help_tags(
 	    // completion.
 	    // Insert a backslash before '~', '$' and '.' to avoid their
 	    // special meaning.
-	    if (d - IObuff > IOSIZE - 10)	// getting too long!?
+	    if (d - IObuff > IOSIZE - 20)	// getting too long!? Increased buffer
 		break;
 	    switch (*s)
 	    {
