patch:
diff --git a/src/help.c b/src/help.c
index 28d914c82..15fcb9aea 100644
--- a/src/help.c
+++ b/src/help.c
@@ -423,10 +423,28 @@ find_help_tags(
 							   && arg[2] != NUL)))
 	{
 	    STRCPY(d, "/\\\\");
-	    STRCPY(d + 3, arg + 1);
+    /* Copy arg+1 into d+3, but do not overflow IObuff */
+    {
+        size_t d_off = 3;
+        size_t maxlen = IOSIZE - d_off - 1; /* leave space for NUL */
+        size_t arglen = STRLEN(arg + 1);
+        if (arglen > maxlen)
+            arglen = maxlen;
+        STRNCPY(d + d_off, arg + 1, arglen);
+        d[d_off + arglen] = NUL;
+    }
 	    // Check for "/\\_$", should be "/\\_\$"
-	    if (d[3] == '_' && d[4] == '$')
-		STRCPY(d + 4, "\\$");
+    if (d[3] == '_' && d[4] == '$') {
+        /* Copy "\\$" into d+4, but do not overflow IObuff */
+        size_t d_off = 4;
+        size_t maxlen = IOSIZE - d_off - 1;
+        const char *src = "\\$";
+        size_t srclen = 2;
+        if (srclen > maxlen)
+            srclen = maxlen;
+        STRNCPY(d + d_off, src, srclen);
+        d[d_off + srclen] = NUL;
+    }
 	}
 	else
 	{
