diff --git a/src/help.c b/src/help.c
index 28d914c82..a40b5cbf2 100644
--- a/src/help.c
+++ b/src/help.c
@@ -423,7 +423,13 @@ find_help_tags(
 							   && arg[2] != NUL)))
 	{
 	    STRCPY(d, "/\\\\");
-	    STRCPY(d + 3, arg + 1);
+    // Copy arg+1 into d+3, but do not overflow IObuff (IOSIZE)
+    // d points to IObuff, which is IOSIZE bytes
+    // Copy up to IOSIZE-4 bytes (3 for prefix, 1 for NUL)
+    size_t prefix_len = 3;
+    size_t max_copy = IOSIZE - prefix_len - 1;
+    STRNCPY(d + prefix_len, arg + 1, max_copy);
+    d[IOSIZE - 1] = '\0';
 	    // Check for "/\\_$", should be "/\\_\$"
 	    if (d[3] == '_' && d[4] == '$')
 		STRCPY(d + 4, "\\$");

