diff --git a/src/misc1.c b/src/misc1.c
index 90cf91474..c5a49d0ae 100644
--- a/src/misc1.c
+++ b/src/misc1.c
@@ -547,6 +547,9 @@ gchar_pos(pos_T *pos)
     // When searching columns is sometimes put at the end of a line.
     if (pos->col == MAXCOL)
 	return NUL;
+    // Check if position is beyond line length to prevent buffer overflow
+    if (pos->col >= ml_get_buf_len(curbuf, pos->lnum))
+	return NUL;
     ptr = ml_get_pos(pos);
     if (has_mbyte)
 	return (*mb_ptr2char)(ptr);
diff --git a/src/register.c b/src/register.c
index 3ca425b45..dbdb5fc69 100644
--- a/src/register.c
+++ b/src/register.c
@@ -1268,8 +1268,11 @@ op_yank(oparg_T *oap, int deleting, int mess)

 		    charwise_block_prep(oap->start, oap->end, &bd, lnum, oap->inclusive);

-		    // make sure bd.textlen is not longer than the text
-		    tmp = (int)STRLEN(bd.textstart);
+	    // make sure bd.textlen is not longer than the text
+	    // Calculate the maximum available length from textstart to end of line
+	    // to prevent reading beyond allocated buffer
+	    colnr_T line_len = ml_get_len(lnum);
+	    tmp = (bd.textcol < line_len) ? (int)(line_len - bd.textcol) : 0;
 		    if (tmp < bd.textlen)
 			bd.textlen = tmp;

