+			    while (r < r_end && *r != NUL && *r == ' ')
+				r++;
+			    if (r > r_end)
+				r = r_end;
+			    if (r >= msgbuf + msgbuflen)
+				pat_len = 0;
+			    else
+				pat_len = (size_t)(msgbuf + msgbuflen - r);
+			    if (pat_len > msgbuflen)
