diff --git a/src/search.c b/src/search.c
index 01c143f69..5ee9f407a 100644
--- a/src/search.c
+++ b/src/search.c
@@ -1549,15 +1549,21 @@ do_search(
 			    vim_free(msgbuf);
 			    msgbuf = r;
 			    // move reversed text to beginning of buffer
-			    while (*r != NUL && *r == ' ')
-				r++;
-			    pat_len = msgbuf + msgbuflen - r;
-			    mch_memmove(msgbuf, r, pat_len);
-			    // overwrite old text
-			    if ((size_t)(r - msgbuf) >= pat_len)
-				vim_memset(r, ' ', pat_len);
-			    else
-				vim_memset(msgbuf + pat_len, ' ', r - msgbuf);
+                size_t revbuflen = STRLEN(msgbuf);
+                char_u *end = msgbuf + revbuflen;
+                while (r < end && *r == ' ')
+                    r++;
+                if (r > end)
+                    r = end;
+                pat_len = (size_t)(end - r);
+                if (pat_len > 0 && pat_len <= revbuflen) {
+                    mch_memmove(msgbuf, r, pat_len);
+                }
+                // overwrite old text
+                if ((size_t)(r - msgbuf) >= pat_len)
+                    vim_memset(r, ' ', pat_len);
+                else
+                    vim_memset(msgbuf + pat_len, ' ', r - msgbuf);
 			}
 		    }
 #endif
