patch:
diff --git a/src/ex_docmd.c b/src/ex_docmd.c
index 26acc07e0..988d3570f 100644
--- a/src/ex_docmd.c
+++ b/src/ex_docmd.c
@@ -3437,16 +3437,27 @@ append_command(char_u *cmd)
     d = IObuff + STRLEN(IObuff);
     while (*s != NUL && d - IObuff < IOSIZE - 7)
     {
-	if (enc_utf8 ? (s[0] == 0xc2 && s[1] == 0xa0) : *s == 0xa0)
-	{
-	    s += enc_utf8 ? 2 : 1;
-	    STRCPY(d, "<a0>");
-	    d += 4;
-	}
-	else
-	    MB_COPY_CHAR(s, d);
-    }
-    *d = NUL;
+        if (enc_utf8 ? (s[0] == 0xc2 && s[1] == 0xa0) : *s == 0xa0)
+        {
+            int needed = 4; // "<a0>"
+            if (d - IObuff + needed >= IOSIZE)
+                break;
+            s += enc_utf8 ? 2 : 1;
+            STRCPY(d, "<a0>");
+            d += 4;
+        }
+        else
+        {
+            int charlen = has_mbyte ? (*mb_ptr2len)(s) : 1;
+            if (d - IObuff + charlen >= IOSIZE)
+                break;
+            MB_COPY_CHAR(s, d);
+        }
+    }
+    if (d - IObuff < IOSIZE)
+        *d = NUL;
+    else
+        IObuff[IOSIZE - 1] = NUL;
 }

 #if defined(FEAT_EVAL) || defined(PROTO)

