patch:
diff --git a/src/ex_docmd.c b/src/ex_docmd.c
index 26acc07e0..aa743aff9 100644
--- a/src/ex_docmd.c
+++ b/src/ex_docmd.c
@@ -3435,18 +3435,26 @@ append_command(char_u *cmd)

     STRCAT(IObuff, ": ");
     d = IObuff + STRLEN(IObuff);
-    while (*s != NUL && d - IObuff < IOSIZE - 7)
-    {
-	if (enc_utf8 ? (s[0] == 0xc2 && s[1] == 0xa0) : *s == 0xa0)
-	{
-	    s += enc_utf8 ? 2 : 1;
-	    STRCPY(d, "<a0>");
-	    d += 4;
-	}
-	else
-	    MB_COPY_CHAR(s, d);
-    }
-    *d = NUL;
+    while (*s != NUL && d - IObuff < IOSIZE - 1) {
+        if ((enc_utf8 ? (s[0] == 0xc2 && s[1] == 0xa0) : *s == 0xa0)) {
+            // Need 5 bytes: 4 for "<a0>" and 1 for NUL
+            if (d - IObuff > IOSIZE - 5)
+                break;
+            s += enc_utf8 ? 2 : 1;
+            STRCPY(d, "<a0>");
+            d += 4;
+        } else {
+            int char_len = enc_utf8 ? utf_ptr2len(s) : (*mb_ptr2len)(s);
+            if (d - IObuff > IOSIZE - (char_len + 1))
+                break;
+            MB_COPY_CHAR(s, d);
+        }
+    }
+    // Ensure NUL-termination and do not write past the buffer
+    if (d - IObuff >= IOSIZE)
+        IObuff[IOSIZE - 1] = NUL;
+    else
+        *d = NUL;
 }

 #if defined(FEAT_EVAL) || defined(PROTO)

