diff --git a/src/ex_docmd.c b/src/ex_docmd.c
index 26acc07e0..e6d508b69 100644
--- a/src/ex_docmd.c
+++ b/src/ex_docmd.c
@@ -3437,14 +3437,26 @@ append_command(char_u *cmd)
     d = IObuff + STRLEN(IObuff);
     while (*s != NUL && d - IObuff < IOSIZE - 7)
     {
-	if (enc_utf8 ? (s[0] == 0xc2 && s[1] == 0xa0) : *s == 0xa0)
-	{
-	    s += enc_utf8 ? 2 : 1;
-	    STRCPY(d, "<a0>");
-	    d += 4;
-	}
-	else
-	    MB_COPY_CHAR(s, d);
+        if (enc_utf8 ? (s[0] == 0xc2 && s[1] == 0xa0) : *s == 0xa0)
+        {
+            // Need 4 bytes for "<a0>" and 1 for NUL
+            if (d - IObuff + 4 < IOSIZE) {
+                s += enc_utf8 ? 2 : 1;
+                STRCPY(d, "<a0>");
+                d += 4;
+            } else {
+                break;
+            }
+        }
+        else {
+            int l = has_mbyte ? (*mb_ptr2len)(s) : 1;
+            // Need l bytes for the char and 1 for NUL
+            if (d - IObuff + l < IOSIZE) {
+                MB_COPY_CHAR(s, d);
+            } else {
+                break;
+            }
+        }
     }
     *d = NUL;
 }

