diff --git a/src/xxd/xxd.c b/src/xxd/xxd.c
index cf8b4ea6a..3978517ba 100644
--- a/src/xxd/xxd.c
+++ b/src/xxd/xxd.c
@@ -1098,6 +1098,15 @@ main(int argc, char *argv[])
           if (hextype == HEX_LITTLEENDIAN)
             c += 1;

+          /* Ensure there is enough space for COLOR_PROLOGUE (5 bytes) */
+          if (c + 5 >= (int)sizeof(l)) {
+            if (c < (int)sizeof(l)) l[c] = '\0';
+            else l[sizeof(l)-1] = '\0';
+            xxdline(fpo, l, autoskip ? nonzero : 1);
+            nonzero = 0;
+            p = 0;
+            c = 0;
+          }
           COLOR_PROLOGUE
           begin_coloring_char(l,&c,e,ebcdic);
 #if defined(__MVS__) && __CHARSET_LIB == 0

