patch:
diff --git a/src/xxd/xxd.c b/src/xxd/xxd.c
index cf8b4ea6a..78397a502 100644
--- a/src/xxd/xxd.c
+++ b/src/xxd/xxd.c
@@ -1090,6 +1090,10 @@ main(int argc, char *argv[])

       if (color)
         {
+          /* Prevent buffer overflow: ensure we never write past LLEN */
+          if (c + 5 >= LLEN)  /* COLOR_PROLOGUE writes 5 bytes */
+            c = LLEN - 6;     /* leave space for at least one char and terminator */
+
           if (hextype == HEX_BITS)
             c += addrlen + 3 + p*12;
           else
@@ -1098,7 +1102,15 @@ main(int argc, char *argv[])
           if (hextype == HEX_LITTLEENDIAN)
             c += 1;

-          COLOR_PROLOGUE
+          if (c + 5 < LLEN) {
+            COLOR_PROLOGUE
+          }
+          else {
+            /* Truncate output to avoid overflow */
+            c = LLEN - 1;
+            l[c] = '\0';
+            goto finish_line;
+          }
           begin_coloring_char(l,&c,e,ebcdic);
 #if defined(__MVS__) && __CHARSET_LIB == 0
           if (e >= 64)
@@ -1108,14 +1120,28 @@ main(int argc, char *argv[])
 #else
           if (ebcdic)
             e = (e < 64) ? '.' : etoa64[e-64];
-          l[c++] = (e > 31 && e < 127) ? e : '.';
+          if (c < LLEN)
+            l[c++] = (e > 31 && e < 127) ? e : '.';
 #endif
-          COLOR_EPILOGUE
+          if (c + 4 < LLEN) {
+            COLOR_EPILOGUE
+          }
+          else {
+            c = LLEN - 1;
+            l[c] = '\0';
+            goto finish_line;
+          }
           n++;
           if (++p == cols)
             {
-              l[c++] = '\n';
-              l[c++] = '\0';
+              if (c + 2 < LLEN) {
+                l[c++] = '\n';
+                l[c++] = '\0';
+              } else {
+                l[LLEN-2] = '\n';
+                l[LLEN-1] = '\0';
+              }
+finish_line:
               xxdline(fpo, l, autoskip ? nonzero : 1);
               nonzero = 0;
               p = 0;

