diff --git a/src/xxd/xxd.c b/src/xxd/xxd.c
index cf8b4ea6a..b0ec85804 100644
--- a/src/xxd/xxd.c
+++ b/src/xxd/xxd.c
@@ -672,6 +672,26 @@ main(int argc, char *argv[])
   unsigned long displayoff = 0;
   static char l[LLEN+1];  /* static because it may be too big for stack */
   char *pp;
+#define L_SAFE_WRITE(expr, needed) \
+  do { \
+    if ((c) + (needed) < (int)sizeof(l)) { expr; } \
+    else { l[sizeof(l)-1] = '\0'; goto finish_line; } \
+  } while (0)
+
+#undef COLOR_PROLOGUE
+#define COLOR_PROLOGUE \
+  L_SAFE_WRITE(l[c++] = '\033', 5); \
+  L_SAFE_WRITE(l[c++] = '[', 4); \
+  L_SAFE_WRITE(l[c++] = '1', 3); \
+  L_SAFE_WRITE(l[c++] = ';', 2); \
+  L_SAFE_WRITE(l[c++] = '3', 1);
+
+#undef COLOR_EPILOGUE
+#define COLOR_EPILOGUE \
+  L_SAFE_WRITE(l[c++] = '\033', 4); \
+  L_SAFE_WRITE(l[c++] = '[', 3); \
+  L_SAFE_WRITE(l[c++] = '0', 2); \
+  L_SAFE_WRITE(l[c++] = 'm', 1);
   char *varname = NULL;
   int addrlen = 9;
   int color = 0;
@@ -1101,22 +1121,28 @@ main(int argc, char *argv[])
           COLOR_PROLOGUE
           begin_coloring_char(l,&c,e,ebcdic);
 #if defined(__MVS__) && __CHARSET_LIB == 0
-          if (e >= 64)
-            l[c++] = e;
-          else
-            l[c++] = '.';
+          if ((c + 1) < (int)sizeof(l)) {
+            if (e >= 64)
+              l[c++] = e;
+            else
+              l[c++] = '.';
+          } else { l[sizeof(l)-1] = '\0'; goto finish_line; }
 #else
           if (ebcdic)
             e = (e < 64) ? '.' : etoa64[e-64];
-          l[c++] = (e > 31 && e < 127) ? e : '.';
+          if ((c + 1) < (int)sizeof(l))
+            l[c++] = (e > 31 && e < 127) ? e : '.';
+          else { l[sizeof(l)-1] = '\0'; goto finish_line; }
 #endif
           COLOR_EPILOGUE
           n++;
           if (++p == cols)
             {
-              l[c++] = '\n';
-              l[c++] = '\0';
-              xxdline(fpo, l, autoskip ? nonzero : 1);
+              if ((c + 2) < (int)sizeof(l)) {
+                l[c++] = '\n';
+                l[c++] = '\0';
+                xxdline(fpo, l, autoskip ? nonzero : 1);
+              } else { l[sizeof(l)-1] = '\0'; goto finish_line; }
               nonzero = 0;
               p = 0;
             }
@@ -1143,6 +1169,8 @@ main(int argc, char *argv[])
               nonzero = 0;
               p = 0;
             }
+finish_line:
+
         }
     }
   if (p)

