patch:
diff --git a/src/tuple.c b/src/tuple.c
index eff4bdccc..3541b66b0 100644
--- a/src/tuple.c
+++ b/src/tuple.c
@@ -518,6 +518,9 @@ eval_tuple(char_u **arg, typval_T *rettv, evalarg_T *evalarg, int do_error)
 	    // Add the first item to the tuple from "rettv"
 	    if (tuple_append_tv(tuple, rettv) == FAIL)
 		return FAIL;
+	    // Clear rettv to avoid double-free when tuple is freed
+	    // The content has been copied to the tuple
+	    init_tv(rettv);
 	}
     }

@@ -594,6 +597,8 @@ eval_tuple(char_u **arg, typval_T *rettv, evalarg_T *evalarg, int do_error)
 failret:
 	if (evaluate)
 	    tuple_free(tuple);
+	// Clear rettv to avoid double-free since tuple_free freed the content
+	init_tv(rettv);
 	return FAIL;
     }
