diff --git a/src/binary-reader-objdump.cc b/src/binary-reader-objdump.cc
index 931db76a..af1a6f81 100644
--- a/src/binary-reader-objdump.cc
+++ b/src/binary-reader-objdump.cc
@@ -1858,6 +1858,11 @@ string_view ObjdumpNames::Get(Index index) const {
 }

 void ObjdumpNames::Set(Index index, string_view name) {
+  // Check for invalid index values that could cause memory issues
+  if (index > 0x10000000) {  // 256MB limit - reasonable for function count
+    return;
+  }
+  
   if (names.size() <= index) {
     names.resize(index + 1);
   }
