index 931db76a..ef206425 100644
--- a/src/binary-reader-objdump.cc
+++ b/src/binary-reader-objdump.cc
@@ -1858,9 +1858,20 @@ string_view ObjdumpNames::Get(Index index) const {
 }

 void ObjdumpNames::Set(Index index, string_view name) {
+  // Validate index to prevent excessive memory allocation
+  if (index > 0x10000000) {  // 256MB limit
+    return;
+  }
+  
+  // Validate string_view before using it
+  if (name.data() == nullptr && name.size() > 0) {
+    return;
+  }
+  
   if (names.size() <= index) {
     names.resize(index + 1);
   }
+  
   names[index] = name.to_string();
 }
