 void ObjdumpNames::Set(Index index, string_view name) {
   if (names.size() <= index) {
+    if (index > 1000000) {  // Sanity check for extremely large index
+      return;
+    }
     names.resize(index + 1);
   }
   names[index] = name.to_string();
