@@ -1858,6 +1858,12 @@ string_view ObjdumpNames::Get(Index index) const {
 }

 void ObjdumpNames::Set(Index index, string_view name) {
+  // Arbitrary sanity limit to prevent OOM or vector corruption
+  static constexpr size_t kMaxNames = 1 << 20; // 1 million entries
+  if (index >= kMaxNames) {
+    // Ignore or log error, but do not attempt to resize
+    return;
+  }
   if (names.size() <= index) {
     names.resize(index + 1);
   }
