@@ -1858,6 +1858,12 @@ string_view ObjdumpNames::Get(Index index) const {
 }

 void ObjdumpNames::Set(Index index, string_view name) {
+  // Arbitrary sanity limit: ignore absurdly large indices to prevent OOM/segfault
+  constexpr size_t MAX_REASONABLE_INDEX = 1000000;
+  if (index > MAX_REASONABLE_INDEX) {
+    // Optionally log or handle error here
+    return;
+  }
   if (names.size() <= index) {
     names.resize(index + 1);
   }
