diff --git a/src/binary-reader-objdump.cc b/src/binary-reader-objdump.cc
index 931db76a..6828a89f 100644
--- a/src/binary-reader-objdump.cc
+++ b/src/binary-reader-objdump.cc
@@ -1858,6 +1858,12 @@ string_view ObjdumpNames::Get(Index index) const {
 }

 void ObjdumpNames::Set(Index index, string_view name) {
+  // Avoid absurdly large indices to prevent OOM or segfault
+  static constexpr size_t kMaxReasonableIndex = 1000000;
+  if (index > kMaxReasonableIndex) {
+    fprintf(stderr, "[wabt] ObjdumpNames::Set: index %u too large, ignoring.\n", index);
+    return;
+  }
   if (names.size() <= index) {
     names.resize(index + 1);
   }
