index 931db76a..dbf5c92a 100644
--- a/src/binary-reader-objdump.cc
+++ b/src/binary-reader-objdump.cc
@@ -1858,6 +1858,12 @@ string_view ObjdumpNames::Get(Index index) const {
 }

 void ObjdumpNames::Set(Index index, string_view name) {
+  // Defensive: avoid excessive allocation or invalid index
+  constexpr size_t kMaxReasonableIndex = 1 << 20; // 1 million
+  if (index >= kMaxReasonableIndex) {
+    // Ignore out-of-bounds set to avoid memory issues/segfault
+    return;
+  }
   if (names.size() <= index) {
     names.resize(index + 1);
   }
