-    names.resize(index + 1);
-  }
-  names[index] = name.to_string();
+  // Arbitrary sanity limit to prevent OOM or segfault on bogus input
+  static const size_t kMaxNames = 1000000;
+  if (index >= kMaxNames) {
+    fprintf(stderr, "ObjdumpNames::Set: index %" PRIindex " is too large, ignoring\n", index);
