patch:
--- a/src/common.cc
+++ b/src/common.cc
@@ -72,13 +72,21 @@
   if (fseek(infile, 0, SEEK_SET) < 0) {
     perror("fseek to beginning failed");
     fclose(infile);
     return Result::Error;
   }

-  out_data->resize(size);
-  if (size != 0 && fread(out_data->data(), size, 1, infile) != 1) {
-    perror("fread failed");
-    fclose(infile);
-    return Result::Error;
-  }
+
+  // Check for unreasonable file size to prevent excessive allocation
+  if (size < 0 || static_cast<unsigned long>(size) > 0x10000000) { // 256MB limit
+    fprintf(stderr, "File size %ld is invalid or too large to read.\n", size);
+    fclose(infile);
+    return Result::Error;
+  }
+
+  out_data->resize(static_cast<size_t>(size));
+  if (size != 0 && fread(out_data->data(), static_cast<size_t>(size), 1, infile) != 1) {
+    perror("fread failed");
+    fclose(infile);
+    return Result::Error;
+  }

