diff --git a/parser.c b/parser.c
index 0edd53bc..de1bc3a3 100644
--- a/parser.c
+++ b/parser.c
@@ -3503,6 +3503,11 @@ xmlParseNCNameComplex(xmlParserCtxtPtr ctxt) {
         xmlFatalErr(ctxt, XML_ERR_NAME_TOO_LONG, "NCName");
         return(NULL);
     }
+    /* Ensure we don't go before the buffer start */
+    if (end - len < ctxt->input->base) {
+        xmlFatalErr(ctxt, XML_ERR_INTERNAL_ERROR, "NCName calculation error");
+        return(NULL);
+    }
     return(xmlDictLookup(ctxt->dict, end - len, len));
 }

