patch:
diff --git a/xpath.c b/xpath.c
index d40bdda6..3a2936b8 100644
--- a/xpath.c
+++ b/xpath.c
@@ -12065,13 +12065,17 @@ xmlXPathIsPositionalPredicate(xmlXPathParserContextPtr ctxt,
 	* like it "[position() < 5]", is also not detected.
 	* Maybe we could rewrite the AST to ease the optimization.
 	*/
-	*maxPos = (int) ((xmlXPathObjectPtr) exprOp->value4)->floatval;
+    double floatval = ((xmlXPathObjectPtr) exprOp->value4)->floatval;
+    /* Check if floatval is within the range of int */
+    if (floatval < (double)INT_MIN || floatval > (double)INT_MAX) {
+        return(0);
+    }
+    *maxPos = (int) floatval;

-	if (((xmlXPathObjectPtr) exprOp->value4)->floatval ==
-	    (float) *maxPos)
-	{
-	    return(1);
-	}
+    if (floatval == (double) *maxPos)
+    {
+        return(1);
+    }
     }
     return(0);
 }
