patch:
diff --git a/HTMLparser.c b/HTMLparser.c
index 69eed2bd..c064c3db 100644
--- a/HTMLparser.c
+++ b/HTMLparser.c
@@ -2514,6 +2514,12 @@ htmlParseNameComplex(xmlParserCtxtPtr ctxt) {
 	NEXTL(l);
 	c = CUR_CHAR(l);
     }
+    /* Bounds check to prevent buffer overflow */
+    if ((len <= 0) || 
+        (ctxt->input->cur - len < ctxt->input->base) ||
+        (ctxt->input->cur - len + len > ctxt->input->end)) {
+        return(NULL);
+    }
     return(xmlDictLookup(ctxt->dict, ctxt->input->cur - len, len));
 }
