patch:
diff --git a/HTMLparser.c b/HTMLparser.c
index 69eed2bd..f5c68312 100644
--- a/HTMLparser.c
+++ b/HTMLparser.c
@@ -2514,6 +2514,13 @@ htmlParseNameComplex(xmlParserCtxtPtr ctxt) {
 	NEXTL(l);
 	c = CUR_CHAR(l);
     }
+    /* Bounds check: ensure we do not read before the buffer base or past the end */
+    if (len < 0 || ctxt->input->cur - len < ctxt->input->base ||
+        ctxt->input->cur > ctxt->input->end ||
+        ctxt->input->cur - len < ctxt->input->base ||
+        ctxt->input->cur - len + len > ctxt->input->end) {
+        return(NULL);
+    }
     return(xmlDictLookup(ctxt->dict, ctxt->input->cur - len, len));
 }
