index c0585fe2..bf938dc9 100644
--- a/dict.c
+++ b/dict.c
@@ -281,6 +281,8 @@ xmlDictAddString(xmlDictPtr dict, const xmlChar *name, unsigned int namelen) {
 #endif
     }
 found_pool:
+    if (pool->end - pool->free < namelen + 1)
+        return(NULL);
     ret = pool->free;
     memcpy(pool->free, name, namelen);
     pool->free += namelen;

